// 1) Leer array histórico de flow (o crear uno nuevo)
let historial = flow.get("historialFechas") || [];


if(msg.payload==true){
  // 2) Determinar la fecha a grabar como Date
  let dateObj;
  if (typeof msg.payload === "string") {
    dateObj = new Date(msg.payload);
  } else if (msg.payload && msg.payload.fecha) {
    dateObj = new Date(msg.payload.fecha);
  } else {
    dateObj = new Date();
  }

  // 3) Formatear en horario de Argentina y estilo dd/mm/yyyy HH:MM:SS
  const nuevaFecha = dateObj.toLocaleString('es-AR', {
    timeZone: 'America/Argentina/Buenos_Aires',
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });

  // 4) Insertar al principio (más reciente primero)
  historial.unshift({ fecha: nuevaFecha });

  // 5) Limitar tamaño a las últimas 20 entradas (opcional)
  const MAX = 20;
  if (historial.length > MAX) {
    historial = historial.slice(0, MAX);
  }
  // 6) Guardar el array actualizado y enviarlo
  flow.set("historialFechas", historial);
}

msg.payload = historial;
return msg;

// Function “Gestionar Alarma”
const presence = msg.payload === true;
const silenciada = flow.get("alarmaSilenciada") || false;

if (presence && !silenciada) {
  // 1) activar buzzer/LED
  let out = { topic:"arqAvanzada/alarma/activar", payload:true };
  // 2) guardar la detección en el historial
  let hist = flow.get("historialFechas")||[];
  hist.unshift( new Date().toLocaleString() );
  flow.set("historialFechas", hist);
  // 3) programar auto-off en 15s
  setTimeout(()=>{
    node.send({ topic:"arqAvanzada/alarma/activar", payload:false });
    flow.set("alarmaSilenciada", false);
  }, 15000);
  return out;
} else {
  // apagar (o no encender si presence=false o ya silenciada)
  flow.set("alarmaSilenciada", false);
  return { topic:"arqAvanzada/alarma/activar", payload:false };
}

